package kr.or.connect.reservation.dao;

public class CommentSqls {
	public static final String GET_COMMENTS_ALL = "SELECT product.description, comment, score, reservation_email, reservation_user_comment.modify_date, save_file_name FROM display_info, reservation_info, reservation_user_comment, reservation_user_comment_image, file_info, product WHERE display_info.id=:id AND display_info.id=reservation_info.display_info_id AND reservation_info.id=reservation_user_comment.reservation_info_id AND reservation_user_comment.id=reservation_user_comment_image.reservation_user_comment_id AND file_info.id=reservation_user_comment_image.file_id AND product.id=display_info.product_id ORDER BY reservation_user_comment.modify_date ASC;";
	public static final String GET_COMMENTS_SOME = "SELECT product.description, comment, score, reservation_email, reservation_user_comment.modify_date, save_file_name FROM product, display_info, reservation_info, reservation_user_comment LEFT OUTER JOIN reservation_user_comment_image ON reservation_user_comment.id=reservation_user_comment_image.reservation_user_comment_id LEFT OUTER JOIN file_info ON reservation_user_comment_image.file_id=file_info.id WHERE display_info.id=:id AND product.id=display_info.product_id AND display_info.id=reservation_info.display_info_id AND reservation_info.id=reservation_user_comment.reservation_info_id ORDER BY reservation_user_comment.modify_date ASC limit 0, 3;";
}
